package loops;
import java.util.*;

public class One {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		if(a<0)
		{
			System.out.println("NEGATIVE");
		}
		else if(a>0)
		{
			System.out.println("Positive");
		}
		else
		{
			System.out.println("zero");
		}
		
	}

}
